import React, { useState, useEffect } from "react";
import "./App.css";
import { Switch, Route, NavLink } from "react-router-dom";
import jwt_decode from "jwt-decode";

function App({ apiFetchFacade, authFacade }) {
  const [loggedIn, setLoggedIn] = useState(false);

  const logout = () => {
    authFacade.logout();
    setLoggedIn(false);
  };
  const login = (user, pass) => {
    authFacade
      .login(user, pass)
      .then((res) => setLoggedIn(true))
      .catch((res) =>
        alert("Status code : " + res.status + " Wrong username or password.")
      );
  };

  return (
    <div className="App">
      {!loggedIn ? (
        <LogIn login={login} />
      ) : (
        <div>
          <LoggedIn apiFetchFacade={apiFetchFacade} />
          <button onClick={logout}>Logout</button>
        </div>
      )}
    </div>
  );
}

function LogIn({ login }) {
  const init = { username: "", password: "" };
  const [loginCredentials, setLoginCredentials] = useState(init);

  const performLogin = (evt) => {
    evt.preventDefault();
    login(loginCredentials.username, loginCredentials.password);
  };
  const onChange = (evt) => {
    setLoginCredentials({
      ...loginCredentials,
      [evt.target.id]: evt.target.value,
    });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onChange={onChange}>
        <input placeholder="User Name" id="username" />
        <input placeholder="Password" id="password" />
        <button onClick={performLogin}>Login</button>
      </form>
    </div>
  );
}

function LoggedIn({ apiFetchFacade }) {
  const [role, setRole] = useState("");
  useEffect(() => {
    var token = localStorage.getItem("jwtToken");
    var decoded = jwt_decode(token);
    setRole(decoded.roles);
  }, []);
  console.log(role);
  return (
    <div>
      <Header role={role} />
      <Switch>
        <Route exact path="/">
          <Home />
        </Route>
        <Route path="/fetch">
          <ApiFetch apiFetchFacade={apiFetchFacade} />
        </Route>
        {role === "admin" && (
          <Route path="/custompage">
            <Custompage />
          </Route>
        )}
        <Route>
          <NoMatch />
        </Route>
      </Switch>
    </div>
  );
}

function Header({ role }) {
  return (
    <div>
      <ul className="header">
        <li>
          <NavLink exact activeClassName="active" to="/">
            Home
          </NavLink>
        </li>
        <li>
          <NavLink activeClassName="active" to="/fetch">
            Api Fetch
          </NavLink>
        </li>
        {role === "admin" && (
          <li>
            <NavLink activeClassName="active" to="/custompage">
              Custom page
            </NavLink>
          </li>
        )}
      </ul>
    </div>
  );
}

function NoMatch() {
  return (
    <div>
      <h3>No match for that route</h3>
    </div>
  );
}

function Capatialize(prop) {
  return prop.charAt(0).toUpperCase() + prop.slice(1);
}

function Home() {
  const [username, setUsername] = useState("");
  const [role, setRole] = useState("");

  useEffect(() => {
    var token = localStorage.getItem("jwtToken");
    var decoded = jwt_decode(token);
    setUsername(Capatialize(decoded.username));
    setRole(decoded.roles);
  }, []);

  return (
    <div>
      <h2>
        Welcome {username} you're logged in with the role: {role}
      </h2>
    </div>
  );
}

function Custompage() {
  return (
    <div>
      <h2>Only admins can see this special and important message</h2>
    </div>
  );
}

function Table(prop) {
  const PlaceId = prop.Places;
  const listItems = PlaceId.map((places) => <li>{places}</li>);
  return (
    <ul>{listItems}</ul>
  ); /*

  return (
    <table>
      <tbody>
        {prop.map((list, i) => (
          <tr key={i}>
            {list.map((num, j) => (
              <td key={j}>{num}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );*/
}

function ApiFetch({ apiFetchFacade }) {
  const [ca3fetch, setCa3fetch] = useState([]);

  useEffect(() => {
    apiFetchFacade()
      .getApiFetch()
      .then((data) => {
        setCa3fetch({ ...data });
      });
  }, []);

  return (
    <div>
      <ul>
        <li>Chuck joke : {ca3fetch.chuckJoke}</li>
        <li>Chuck joke url : {ca3fetch.chuckJokeURL}</li>
        <li>Dad joke : {ca3fetch.dadJoke}</li>
        <li>Dad joke url : {ca3fetch.dadJokeURL}</li>
        <li>{JSON.stringify(ca3fetch.scanner)}</li>
        <Table Table={ca3fetch.scanner} />
        <li>
          Dog Message :{" "}
          <img src={ca3fetch.dogDTOMessage} alt={ca3fetch.dogDTOMessage}></img>
        </li>
      </ul>
    </div>
  );
}

export default App;
