import React, { useState, useEffect } from "react";
import "./App.css";
import { Switch, Route, NavLink } from "react-router-dom";
import jwt_decode from "jwt-decode";

function App({ apiFetchFacade, authFacade }) {
  let token = localStorage.getItem("jwtToken");

  const [loggedIn, setLoggedIn] = useState(token !== undefined);
  const [role, setRole] = useState("");

  const logout = () => {
    authFacade.logout();
    setLoggedIn(false);
    updateRoles();
  };

  const login = (user, pass) => {
    authFacade
      .login(user, pass)
      .then((res) => setLoggedIn(true))
      .then((res) => updateRoles())
      .catch((res) =>
        alert("Status code : " + res.status + " Wrong username or password.")
      );
  };

  function updateRoles() {
    token = localStorage.getItem("jwtToken");
    if (token) {
      var decoded = jwt_decode(token);
      setRole(decoded.roles);
    } else {
      setRole(null);
    }
  }

  useEffect(() => {
    token = localStorage.getItem("jwtToken");
    if (token) {
      var decoded = jwt_decode(token);
      setLoggedIn(true);
      setRole(decoded.roles);
    }
  }, []);

  console.log(role);
  return (
    <div className="App">
      <Header loggedIn={loggedIn} role={role} logout={logout} />
      {loggedIn && (
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          {role.includes("user") && (
            <Route path="/fetch">
              <ApiFetch apiFetchFacade={apiFetchFacade} />
            </Route>
          )}
          {role.includes("admin") && (
            <Route path="/custompage">
              <Custompage />
            </Route>
          )}
          <Route>
            <NoMatch />
          </Route>
        </Switch>
      )}
      {!loggedIn && (
        <Switch>
          <Route path="/login">
            <LogIn login={login} />
          </Route>
          <Route>
            <LogIn login={login} />
          </Route>
        </Switch>
      )}
    </div>
  );
}

function LogIn({ login }) {
  const init = { username: "", password: "" };
  const [loginCredentials, setLoginCredentials] = useState(init);

  const performLogin = (evt) => {
    evt.preventDefault();
    login(loginCredentials.username, loginCredentials.password);
  };
  const onChange = (evt) => {
    setLoginCredentials({
      ...loginCredentials,
      [evt.target.id]: evt.target.value,
    });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onChange={onChange}>
        <input placeholder="User Name" id="username" />
        <input placeholder="Password" id="password" />
        <button onClick={performLogin}>Login</button>
      </form>
    </div>
  );
}

function Header({ role, loggedIn, logout }) {
  return (
    <div>
      <ul className="header">
        <li>
          <NavLink exact activeClassName="active" to="/">
            Home
          </NavLink>
        </li>

        {loggedIn ? (
          <>
            {role.includes("admin") && (
              <li>
                <NavLink activeClassName="active" to="/custompage">
                  Custom page
                </NavLink>
              </li>
            )}
            {role.includes("user") && (
              <li>
                <NavLink activeClassName="active" to="/fetch">
                  Api Fetch
                </NavLink>
              </li>
            )}

            <li>
              <NavLink activeClassName="active" onClick={logout} to="/login">
                Logout
              </NavLink>
            </li>
          </>
        ) : (
          <li>
            <NavLink activeClassName="active" to="/login">
              Login
            </NavLink>
          </li>
        )}
      </ul>
    </div>
  );
}

function NoMatch() {
  return (
    <div>
      <h3>No match for that route</h3>
    </div>
  );
}

function Capatialize(prop) {
  return prop.charAt(0).toUpperCase() + prop.slice(1);
}

function Home() {
  const [username, setUsername] = useState("");
  const [role, setRole] = useState("");

  useEffect(() => {
    var token = localStorage.getItem("jwtToken");
    var decoded = jwt_decode(token);
    setUsername(Capatialize(decoded.username));
    setRole(decoded.roles);
  }, []);

  return (
    <div>
      {!role === "" && (
        <h2>
          Welcome {username} you're logged in with the role: {role}
        </h2>
      )}
      {role === "" && <h2>Welcome. Please log in. {role}</h2>}
    </div>
  );
}

function Custompage() {
  return (
    <div>
      <h2>Only admins can see this special and important message</h2>
    </div>
  );
}

function Table(props) {
  if (
    props === undefined ||
    props === null ||
    props.scanner === undefined ||
    props.scanner.Places === undefined
  )
    return <></>;
  return (
    <table>
      <thead>
        <tr>
          <th>PlaceId</th>
          <th>PlaceName</th>
          <th>CountryId</th>
        </tr>
      </thead>
      <tbody>{props.scanner.Places.map((place) => DisplayPlace(place))}</tbody>
    </table>
  );
}

function DisplayPlace(place) {
  return (
    <tr key={place.PlaceId}>
      <td>{place.PlaceId}</td>
      <td>{place.PlaceName}</td>
      <td>{place.CountryId}</td>
    </tr>
  );
}

function ApiFetch({ apiFetchFacade }) {
  const [ca3fetch, setCa3fetch] = useState([]);

  useEffect(() => {
    apiFetchFacade()
      .getApiFetch()
      .then((data) => {
        setCa3fetch({ ...data });
      });
  }, [apiFetchFacade]);

  return (
    <div>
      <ul>
        <li>Chuck joke : {ca3fetch.chuckJoke}</li>
        <li>Chuck joke url : {ca3fetch.chuckJokeURL}</li>
        <li>Dad joke : {ca3fetch.dadJoke}</li>
        <li>Dad joke url : {ca3fetch.dadJokeURL}</li>
        <Table scanner={ca3fetch.scanner} />
        <li>
          Dog Message :{" "}
          <img src={ca3fetch.dogDTOMessage} alt={ca3fetch.dogDTOMessage}></img>
        </li>
      </ul>
    </div>
  );
}

export default App;
